import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import Header from './Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; 
import { faGithub, faLinkedin } from '@fortawesome/free-brands-svg-icons'; 

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', formData, 'YOUR_USER_ID')
      .then((response) => {
        alert('Message sent successfully!');
        setFormData({ name: '', email: '', message: '' }); // Reset form after successful submission
      }, (error) => {
        alert('Failed to send message, please try again.');
      });
  };

  return (
    <div className="contact-container">
      <Header />
      <div className="contact-content">
        <h1>Contact Me</h1>
        <p>
          Feel free to reach out to me if you have any questions, collaboration ideas, or just want to say hello!
        </p>

        {/* Adding your profile picture */}
        <div className="profile-picture">
          <img src="your-image-url.jpg" alt="Chien Huang" />
        </div>

        <form className="contact-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input 
              type="text" 
              id="name" 
              name="name" 
              value={formData.name} 
              onChange={handleChange}
              placeholder="Your Name" 
              required 
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input 
              type="email" 
              id="email" 
              name="email" 
              value={formData.email} 
              onChange={handleChange}
              placeholder="Your Email" 
              required 
            />
          </div>

          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea 
              id="message" 
              name="message" 
              rows="5" 
              value={formData.message} 
              onChange={handleChange}
              placeholder="Your Message" 
              required 
            ></textarea>
          </div>

          <button type="submit" className="submit-button">Send Message</button>
        </form>

        {/* Adding GitHub and LinkedIn links */}
        <div className="social-links">
            <a href="https://github.com/ChienHuang0818" target="_blank" rel="noopener noreferrer" className="icon">
                <FontAwesomeIcon icon={faGithub} size="3x" />
            </a>
            <a href="https://linkedin.com/in/chien-huang-sarah" target="_blank" rel="noopener noreferrer" className="icon">
                <FontAwesomeIcon icon={faLinkedin} size="3x" />
            </a>
        </div>
      </div>
    </div>
  );
};

export default Contact;
